import React, { useEffect, useState } from 'react';
import Clients from './components/Clients';
import Devices from './components/Devices';
import Tickets from './components/Tickets';
import Login from './components/Login';
import ProtectedRoute from './components/ProtectedRoute';
import ClientPage from './pages/ClientPage';
import EmployeePage from './pages/EmployeePage';
import AdminPage from './pages/AdminPage';
import { me, removeToken } from './auth';

export default function App(){
  const [user, setUser] = useState(null);

  useEffect(()=>{
    (async ()=>{
      try{
        const u = await me();
        setUser(u);
      }catch(e){ setUser(null); }
    })();
  }, []);

  const onLogin = (u)=> setUser(u);
  const logout = ()=>{ removeToken(); setUser(null); };

  return (
    <div className="p-6 font-sans bg-gray-100 min-h-screen">
      <header className="flex items-center justify-between mb-6">
        <h1 className="text-2xl font-bold">Atelier de Réparation</h1>
        <div>
          {user ? (
            <div className="flex items-center gap-2">
              <div>{user.name} ({user.role})</div>
              <button onClick={logout} className="text-sm px-3 py-1 bg-red-500 text-white rounded">Déconnexion</button>
            </div>
          ) : (
            <div>Non connecté</div>
          )}
        </div>
      </header>

      {!user ? (
        <Login onLogin={onLogin} />
      ) : (
        <div className="grid grid-cols-4 gap-4">
          <div className="col-span-1 bg-white p-4 rounded shadow">
            <Clients />
            <Devices />
          </div>
          <div className="col-span-3 bg-white p-4 rounded shadow">
            <h2 className="text-lg font-bold mb-2">Tableau de bord</h2>
            <ProtectedRoute user={user} allowedRoles={["client"]}>
              <ClientPage />
            </ProtectedRoute>
            <ProtectedRoute user={user} allowedRoles={["employee"]}>
              <EmployeePage />
            </ProtectedRoute>
            <ProtectedRoute user={user} allowedRoles={["admin"]}>
              <AdminPage />
            </ProtectedRoute>
            <Tickets />
          </div>
        </div>
      )}
    </div>
  );
}
