import React from 'react';

export default function ProtectedRoute({ user, allowedRoles, children }){
  if(!user) return <div className="p-4">Accès non autorisé — veuillez vous connecter.</div>;
  if(allowedRoles && !allowedRoles.includes(user.role)) return <div className="p-4">Accès refusé — rôle insuffisant.</div>;
  return children;
}
